const tabs={};let active,current=document.querySelector(".tab.active")?.id;function load(t){t!==current&&(current=t,location.hash=t,document.querySelector(".tabs .active")?.classList.remove("active"),tabs[t]?.classList.add("active"),document.querySelector(".tab.active")?.classList.remove("active"),document.querySelector(".tab#"+t)?.classList.add("active"))}for(const t of document.querySelector(".tabs")?.children){const e=t.href.split("#")[1];tabs[e]=t,t.addEventListener("click",(t=>{load(e),setTimeout((()=>window.scrollTo(0,0)))}))}const getHash=()=>location.hash.slice(1)||current;load(getHash()),window.addEventListener("popstate",(()=>load(getHash()))),window.history&&(window.history.scrollRestoration="manual");